import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from './ui/Button';
import ThemeToggle from './ui/ThemeToggle';

const Layout = ({ children }) => {
  const navigate = useNavigate();
  const isAuthenticated = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="min-h-screen flex flex-col bg-background text-foreground">
      <header className="bg-primary text-primary-foreground p-4">
        <div className="container mx-auto flex justify-between items-center">
          <Link to="/" className="text-2xl font-bold flex items-center">
            <img src="/logo.svg" alt="JobConnect Logo" className="h-8 w-8 mr-2" />
            JobConnect
          </Link>
          <nav className="flex items-center space-x-4">
            <Link to="/jobs" className="hover:underline">Jobs</Link>
            <Link to="/about" className="hover:underline">About</Link>
            <Link to="/support" className="hover:underline">Support</Link>
            {isAuthenticated ? (
              <>
                <Link to="/dashboard" className="hover:underline">Dashboard</Link>
                <Link to="/profile" className="hover:underline">Profile</Link>
                <Button onClick={handleLogout} variant="outline" className="py-1 px-2 text-sm">
                  Logout
                </Button>
              </>
            ) : (
              <Link to="/" className="hover:underline">Login</Link>
            )}
            <ThemeToggle />
          </nav>
        </div>
      </header>
      <main className="flex-grow container mx-auto px-4 py-8">
        {children}
      </main>
      <footer className="bg-secondary text-secondary-foreground p-4">
        <div className="container mx-auto text-center">
          Â© 2023 JobConnect. All rights reserved.
        </div>
      </footer>
    </div>
  );
};

export default Layout;
